// Chart.js ダミーファイル - 基本的なChart機能を提供
window.Chart = {
    register: function() {},
    defaults: {
        global: {
            responsive: true
        }
    }
};

// Chart コンストラクタ
window.Chart = function(ctx, config) {
    this.ctx = ctx;
    this.config = config;
    this.data = config.data || {};
    this.options = config.options || {};
    
    // ダミーのcanvasを作成
    if (ctx && ctx.getContext) {
        const context = ctx.getContext('2d');
        context.fillStyle = '#f0f0f0';
        context.fillRect(0, 0, ctx.width, ctx.height);
        context.fillStyle = '#333';
        context.font = '16px Arial';
        context.textAlign = 'center';
        context.fillText('Chart Placeholder', ctx.width/2, ctx.height/2);
    }
    
    return this;
};

// Chart の静的メソッド
window.Chart.register = function() {};
window.Chart.defaults = {
    global: {
        responsive: true
    }
};

// プロトタイプメソッド
window.Chart.prototype = {
    update: function() {},
    destroy: function() {},
    resize: function() {}
};

console.log('Chart.js ダミーライブラリが読み込まれました');